// 渲染功能
// 1.提供数据-提供在公共的父组件
// 2.通过父组件将数据传递给TodoMain
// 3.利用v-for渲染
export default {
  components: {
    TodoFooter,
    TodoHeader,
    TodoMain
  },
  methods: {
    handleAdd(todoName) {
      this.list.unshift({
        id: +new Date(),
        name: todoName
      });
    }
  },
  data() {
    return {
      list: [{
        id: 1,
        name: '打篮球'
      }, {
        id: 1,
        name: '吃饭'
      }, {
        id: 1,
        name: '看电影'
      }, {
        id: 1,
