import Vue from 'vue'
import App from './App.vue'
// 路由的使用步骤（5+2）
// 1.下载 3.6.5
// 2.引入
// 3.安装注册Vue.use(Vue插件)
// 4.创建路由对象
// 5.注入到new Vue中
import VueRouter from 'vue-router'
Vue.use(VueRouter)//VueRouter插件初始化
const router = new VueRouter()
Vue.config.productionTip = false


new Vue({
  render: h => h(App),
  router
}).$mount('#app')
